func RemoveTag(raw string) string {
	startingScriptTag := "<script"
	endingScriptTag := "</script>"
	script := ""
	for {
		startingScriptTagIndex := strings.Index(raw, startingScriptTag)
		endingScriptTagIndex := strings.Index(raw, endingScriptTag)

		if startingScriptTagIndex > -1 && endingScriptTagIndex > -1 {
			script = raw[startingScriptTagIndex : endingScriptTagIndex+len(endingScriptTag)]
			raw = strings.Replace(raw, script, "", 1)
			continue
		}

		break
	}
	return raw

}
